#!/usr/bin/env python3
import webview
import threading
from flask import Flask, render_template

app = Flask(__name__, template_folder='templates', static_folder='static')

@app.route('/')
def index():
    return render_template('index.html')

@app.route('/api/hello')
def hello():
    return {'message': 'Hello from Python pywebview!'}

def start_server():
    app.run(port=5000, debug=False)

if __name__ == '__main__':
    t = threading.Thread(target=start_server)
    t.daemon = True
    t.start()

    webview.create_window('${PROJECT_NAME}', 'http://localhost:5000', width=800, height=600)
    webview.start()
